  RunnablePassthrough.assign({  
    query: sqlQueryGeneratorChain,  
  }),  
  {  
    schema: async

yGeneratorChain,  
  }),  
  {  
    schema: async () => db.getTableInfo(),  
    question: (input) => input.question,  
    query: (input) => input.query,  
    response: (input) => db.run(input.query),  
  },  
  finalResponsePrompt,  
